            //toggleDress={() => toggleDress(dress.id)}
          />
        ))}
      </ul>

      <h3> Add a Customer </h3>
      {<CustomerForm customerAction={createCustomer} />}

      <h3> Customers </h3>
      <ul>
        {appState.customers.map((customer) => (
          <Customer
            id={customer.id}
            first_name={customer.first_name}
            last_name={customer.last_name}
